import { gql } from 'apollo-boost'

const addBookMutation = gql`
  mutation AddBook($name: String, $genre: String, $authorId: ID) {
    addBook(name: $name, genre: $genre, authorId: $authorId) {
      name
      id
    }
  }
`
const signup = gql`
  mutation signup(
    $firstname: String
    $lastname: String
    $email: String
    $password: String
  ) {
    signup(
      firstname: $firstname
      lastname: $lastname
      email: $email
      password: $password
    ) {
      firstname
      lastname
    }
  }
`

export { addBookMutation, signup }
